var canvas=document.querySelector("canvas");canvas.width=window.innerWidth;canvas.height=window.innerHeight;var c=canvas.getContext("2d");var maxRadius=40;var minRadius=2;var numCircles=400;var colorArray=["#AEF3E7","#8EE3EF","#37718E","#C33C54"];center={x:window.innerWidth/2,y:window.innerHeight/2};window.addEventListener("resize",function(){canvas.width=window.innerWidth;canvas.height=window.innerHeight;init()});var circleArray=[];function init(){center={x:window.innerWidth/2,y:window.innerHeight/2};circleArray=[];for(var i=0;i<numCircles;i++){var n=Math.random()*3+1;var t=Math.random()*(innerWidth-n*2)+n;var a=Math.random()-.5;var r=Math.random()*(innerHeight-n*2)+n;var e=Math.random()-.5;circleArray.push(new Circle(t,r,a,e,n))}}init();var x=Math.random()*innerWidth;var dx=(Math.random()-.5)*8;var y=Math.random()*innerHeight;var dy=(Math.random()-.5)*8;var radius=30;function Circle(i,n,t,a,r){this.x=i;this.y=n;this.dx=t;this.dy=a;this.radius=r;this.minRadius=r;this.color=colorArray[Math.floor(Math.random()*colorArray.length)];this.draw=function(){c.beginPath();c.arc(this.x,this.y,this.radius,0,Math.PI*2,false);c.fillStyle=this.color;c.fill()};this.update=function(){if(this.x+this.radius>innerWidth||this.x-this.radius<0){this.dx=-this.dx}if(this.y+this.radius>innerHeight||this.y-this.radius<0){this.dy=-this.dy}this.x+=this.dx;this.y+=this.dy;var i=center.x-this.x,n=center.y-this.y,t=Math.sqrt(Math.pow(i,2)+Math.pow(n,2));if(t<=150){if(this.radius<maxRadius){this.radius+=1}}else if(this.radius>minRadius){this.radius-=1}this.draw()}}for(var i=0;i<numCircles;i++){var radius=Math.random()*3+1;var x=Math.random()*(innerWidth-radius*2)+radius;var dx=Math.random()-.5;var y=Math.random()*(innerHeight-radius*2)+radius;var dy=Math.random()-.5;circleArray.push(new Circle(x,y,dx,dy,radius))}console.log(circleArray);function animate(){requestAnimationFrame(animate);c.clearRect(0,0,innerWidth,innerHeight);for(var i=0;i<circleArray.length;i++){circleArray[i].update()}}animate();$(document).ready(function(){$menuBtn=$(".menuBtn");$transition=$("#transition");$infoPage=$("#infoPage");$loader=$("#loader");$loaderTransition=$("#loaderTransition");$loadingText=$(".loadingText");$gridLeftItem=$(".grid-leftItem");if(window.innerWidth>800){startup($loader,$loaderTransition,$loadingText,$gridLeftItem)}else{mobileStartup($gridLeftItem)}$menuBtn.on("click",function(){menuBtnToggle($menuBtn);openInfoPg($transition,$infoPage)})});function mobileStartup(i){i.toggleClass("finished")}function startup(i,n,t,a){var r="Vishal Shah";var e=r.split("");var s=e.length;var o=100;var d=1;var h=setInterval(function(){t.append(String(e[d]));d++;if(d===s)clearInterval(h)},o);setTimeout(function(){i.toggleClass("finished");a.toggleClass("finished")},s*o)}function menuBtnToggle(i){i.toggleClass("menuSelected")}function openInfoPg(i,n){i.toggleClass("transitioning");setTimeout(function(){n.toggleClass("openedInfo")},50)}